def day1 input
  floor = 0
  input.each_char do |char|
    if char == "("
      floor += 1
    elsif char == ")"
      floor -= 1
    end
  end
  p floor
end

def day1v2 input
  floor = 0
  position = 0
  input.each_char do |char|
    if char == "("
      floor += 1
      position += 1
    elsif char == ")"
      floor -= 1
      position += 1
    end

    if floor == -1
      p position
    end
  end
end

